import React, { useState } from 'react';
import './App.css';

// Secret word with no duplicate letters
const SECRET_WORD = 'PLANT';

const App = () => {
  const [inputs, setInputs] = useState(Array(5).fill(''));
  const [attempts, setAttempts] = useState([]);
  const [error, setError] = useState('');
  const [gameOver, setGameOver] = useState(false);

  const checkDuplicate = (word) => {
    return new Set(word).size !== word.length;
  };

  const handleChange = (value, index) => {
    // Allow only one character and letters only
    if (value.length > 1 || /[^a-zA-Z]/.test(value)) return;

    const newInputs = [...inputs];
    newInputs[index] = value.toUpperCase();
    setInputs(newInputs);
    setError(''); // Clear error when user types

    // Auto-focus on the next box
    if (value && index < 4) {
      document.getElementById(`box-${index + 1}`).focus();
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const guess = inputs.join('');

    // Check for duplicates
    if (checkDuplicate(guess)) {
      setError('Duplicate letters are not allowed!');
      return;
    }

    // Check word length
    if (guess.length !== 5) {
      setError('Please enter a 5-letter word!');
      return;
    }

    // Check correctness
    let correct = 0;
    let misplaced = 0;
    let incorrect = 0;

    guess.split('').forEach((letter, index) => {
      if (SECRET_WORD[index] === letter) {
        correct++;
      } else if (SECRET_WORD.includes(letter)) {
        misplaced++;
      } else {
        incorrect++;
      }
    });

    setAttempts([...attempts, { guess, correct, misplaced, incorrect }]);
    setInputs(Array(5).fill(''));

    // Check for win or lose condition
    if (correct === 5) {
      setGameOver(true);
    } else if (attempts.length + 1 === 10) {
      setGameOver(true);
    }
  };

  return (
    <div className="game-container">
      <h1>Word Guess Game</h1>

      {attempts.map((attempt, index) => (
        <div key={index} className="attempt-row">
          <span className="word">{attempt.guess}</span>
          <span className="result green">{attempt.correct}</span>
          <span className="result yellow">{attempt.misplaced}</span>
          <span className="result red">{attempt.incorrect}</span>
        </div>
      ))}

      {!gameOver && (
        <form onSubmit={handleSubmit}>
          <div className="input-boxes">
            {inputs.map((letter, index) => (
              <input
                key={index}
                id={`box-${index}`}
                type="text"
                maxLength="1"
                value={letter}
                onChange={(e) => handleChange(e.target.value, index)}
                required
              />
            ))}
          </div>
          <button type="submit" className="submit-btn">Submit</button>
          {error && <div className="error">{error}</div>}
        </form>
      )}

      {gameOver && (
        <div className="game-over">
          {attempts[attempts.length - 1].correct === 5 
            ? <p>You did great!</p>
            : <p>Better luck next time! The word was: {SECRET_WORD}</p>
          }
        </div>
      )}
    </div>
  );
};

export default App;
