import React, { useState, useRef, useEffect } from 'react';
import wordExists from 'word-exists';
import './App.css';

const SECRET_WORD = 'PLANT';

const App = () => {
  const [inputs, setInputs] = useState(Array(5).fill(''));
  const [guesses, setGuesses] = useState([]);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const inputRefs = useRef([]);

  useEffect(() => {
    inputRefs.current[0].focus();
  }, []);

  const handleChange = (index, value) => {
    if (/^[A-Za-z]?$/.test(value)) {
      const newInputs = [...inputs];
      newInputs[index] = value.toUpperCase();
      setInputs(newInputs);

      if (value && index < 4) {
        inputRefs.current[index + 1].focus();
      }
    }
  };

  const handleKeyDown = (index, event) => {
    if (event.key === 'Backspace' && !inputs[index] && index > 0) {
      inputRefs.current[index - 1].focus();
    }
    if (event.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleSubmit = () => {
    const word = inputs.join('');
    if (word.length < 5) {
      setError('Please enter a 5-letter word.');
      return;
    }

    if (new Set(inputs).size !== inputs.length) {
      setError('Duplicate letters are not allowed.');
      return;
    }

    if (!wordExists(word.toLowerCase())) {
      setError('This word does not exist.');
      return;
    }

    setError('');
    const correctPositions = inputs.filter((letter, i) => letter === SECRET_WORD[i]).length;
    const correctLetters = inputs.filter((letter) => SECRET_WORD.includes(letter)).length - correctPositions;

    const newGuesses = [...guesses, { word, correctPositions, correctLetters }];
    setGuesses(newGuesses);

    setInputs(Array(5).fill(''));
    inputRefs.current[0].focus();

    if (newGuesses.length === 10 && word !== SECRET_WORD) {
      setShowModal(true);
    }
    if (correctPositions === 5) {
      setShowModal(true);
    }
  };

  const resetGame = () => {
    setInputs(Array(5).fill(''));
    setGuesses([]);
    setError('');
    setShowModal(false);
    inputRefs.current[0].focus();
    setShowModal(false);
  setGuessArray([]);
  setCurrentGuess(["", "", "", "", ""]);
  setError("");
  };

  return (
    <div className="game-container">
      <h1>Word Guess Game</h1>
      <div className="guesses">
        {guesses.map((guess, index) => (
          <div key={index} className="guess">
            <span className="word">{guess.word}</span>
            <div className="feedback">
              {guess.correctPositions > 0 && (
                <div className="circle green">{guess.correctPositions}</div>
              )}
              {guess.correctLetters > 0 && (
                <div className="circle yellow">{guess.correctLetters}</div>
              )}
              {guess.correctPositions + guess.correctLetters === 0 && (
                <div className="circle red">0</div>
              )}
            </div>
          </div>
        ))}
      </div>
      <div className="input-container">
        {inputs.map((value, index) => (
          <input
            key={index}
            type="text"
            maxLength="1"
            value={value}
            ref={(el) => (inputRefs.current[index] = el)}
            onChange={(e) => handleChange(index, e.target.value)}
            onKeyDown={(e) => handleKeyDown(index, e)}
          />
        ))}
      </div>
      {error && <div className="error">{error}</div>}

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Try Again!</h2>
            <button onClick={resetGame}>Restart</button>
          </div>
        </div>
      )}
      {showModal && (
  <div className="modal">
    <div className="modal-content">
      <h2>Congratulations! 🎉</h2>
      <p>You are a genius!</p>
      <button onClick={resetGame}>Play Again</button>
    </div>
  </div>
)}
    </div>
  );
};

export default App;
