import React, { useState, useEffect } from "react";
import "./App.css";
import wordList from "word-list-json";

const getRandomWord = () => {
  const filteredWords = wordList.filter(
    (word) => word.length === 5 && new Set(word).size === 5
  );
  return filteredWords[
    Math.floor(Math.random() * filteredWords.length)
  ].toUpperCase();
};

const randomWord = ''

const App = () => {
  const [targetWord, setTargetWord] = useState(getRandomWord());
  const [currentGuess, setCurrentGuess] = useState(["", "", "", "", ""]);
  const [guessArray, setGuessArray] = useState([]);
  const [error, setError] = useState("");
  const [activeBox, setActiveBox] = useState(0);
  const [showModal, setShowModal] = useState(false);

  console.log("filtered word", targetWord)

  const handleInputChange = (e, index) => {
    const value = e.target.value.toUpperCase();
    if (/^[A-Z]?$/.test(value)) {
      const newGuess = [...currentGuess];
      newGuess[index] = value;
      setCurrentGuess(newGuess);

      if (value !== "" && index < 4) {
        setActiveBox(index + 1);
      }
    }
  };

  const handleKeyDown = (e, index) => {
    if (e.key === "Backspace") {
      const newGuess = [...currentGuess];
      newGuess[index] = "";
      setCurrentGuess(newGuess);

      if (index > 0 && currentGuess[index] === "") {
        setActiveBox(index - 1);
      }
    }

    if (e.key === "Enter" && currentGuess.join("").length === 5) {
      handleSubmit();
    }
  };

  const handleSubmit = () => {
    const guess = currentGuess.join("");
    if (new Set(guess).size !== 5) {
      setError("Duplicate letters not allowed");
      return;
    }

    if (!wordList.includes(guess.toLowerCase())) {
      setError("This word doesn't exist");
      return;
    }

    setError("");

    let correctPosition = 0;
    let wrongPosition = 0;

    for (let i = 0; i < 5; i++) {
      if (guess[i] === targetWord[i]) {
        correctPosition++;
      } else if (targetWord.includes(guess[i])) {
        wrongPosition++;
      }
    }

    setGuessArray([
      ...guessArray,
      { guess, correctPosition, wrongPosition },
    ]);

    setCurrentGuess(["", "", "", "", ""]);
    setActiveBox(0);

    if (correctPosition === 5) {
      setShowModal(true);
    }

    if (guessArray.length + 1 === 10 && correctPosition !== 5) {
      setTimeout(() => {
        alert("Try Again! The correct word was: " + targetWord);
        resetGame();
      }, 1000);
    }
  };

  const resetGame = () => {
    setShowModal(false);
    setTargetWord(getRandomWord());
    setGuessArray([]);
    setCurrentGuess(["", "", "", "", ""]);
    setError("");
    setActiveBox(0);
  };

  useEffect(() => {
    const inputBox = document.getElementById(`box-${activeBox}`);
    if (inputBox) {
      inputBox.focus();
    }
  }, [activeBox]);

  return (
    <div className="App">
      <h1>Word Guess Game</h1>
      <div className="results">
        {guessArray.map((item, index) => (
          <div key={index} className="result">
            <span>{item.guess}</span>
            {item.correctPosition > 0 && (
              <div className="circle green">{item.correctPosition}</div>
            )}
            {item.wrongPosition > 0 && (
              <div className="circle yellow">{item.wrongPosition}</div>
            )}
            {item.correctPosition === 0 && item.wrongPosition === 0 && (
              <div className="circle red">0</div>
            )}
          </div>
        ))}
      </div>
      <div className="input-row">
        {currentGuess.map((letter, index) => (
          <input
            key={index}
            id={`box-${index}`}
            type="text"
            maxLength="1"
            value={letter}
            onChange={(e) => handleInputChange(e, index)}
            onKeyDown={(e) => handleKeyDown(e, index)}
          />
        ))}
      </div>
      {error && <p className="error">{error}</p>}

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Congratulations! ðŸŽ‰</h2>
            <p>You are a genius!</p>
            <button onClick={resetGame}>Play Again</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
